// Professional BAS mock data generator for portfolio demonstration
// Simulates realistic data center cooling system with proper engineering values

const now = new Date();
const timeString = now.toLocaleTimeString('en-US', { hour12: false });
const timestamp = now.toISOString();

// Simulation state management
if (!context.simulation) {
    context.simulation = {
        time_s: 0,
        setpoint_f: 72.0,
        room_temp_f: 72.1,
        load_kw: 85.0,
        pid_integral: 0,
        crac_states: {
            'CRAC-01': { output: 0, runtime_h: 1247, starts: 23, role: 'LEAD' },
            'CRAC-02': { output: 0, runtime_h: 892, starts: 41, role: 'LAG' },
            'CRAC-03': { output: 0, runtime_h: 156, starts: 8, role: 'STANDBY' }
        },
        scenario: 'baseline',
        faults: {},
        alarms: [],
        kpi_history: []
    }
}

const sim = context.simulation;
sim.time_s += 5; // 5 second intervals

// Temperature simulation with realistic dynamics
const load_factor = 1.0 + 0.1 * Math.sin(sim.time_s / 300); // 5-minute load cycle
sim.load_kw = 85 * load_factor;

// PID Controller simulation
const error_f = sim.room_temp_f - sim.setpoint_f;
const kp = 2.5, ki = 0.1, kd = 0.05;

const p_term = kp * error_f;
sim.pid_integral += error_f * (5/60); // 5 second interval
const i_term = ki * sim.pid_integral;
const d_term = kd * (error_f - (sim.last_error || 0)) / (5/60);
sim.last_error = error_f;

const pid_output = Math.max(0, Math.min(100, p_term + i_term + d_term + 50)); // 50% bias

// CRAC staging logic
let lead_output = 0, lag_output = 0, standby_output = 0;
if (pid_output > 10) {
    lead_output = Math.min(100, pid_output);
    if (pid_output > 80) {
        lag_output = Math.min(100, (pid_output - 80) * 5);
        if (pid_output > 95) {
            standby_output = Math.min(100, (pid_output - 95) * 10);
        }
    }
}

sim.crac_states['CRAC-01'].output = lead_output;
sim.crac_states['CRAC-02'].output = lag_output;
sim.crac_states['CRAC-03'].output = standby_output;

// Temperature dynamics
const total_cooling = (lead_output * 0.35 + lag_output * 0.30 + standby_output * 0.25) / 100;
const temp_change = (sim.load_kw * 0.012 - total_cooling * 25) * (5/3600); // ¬∞F per 5 seconds
sim.room_temp_f += temp_change + (Math.random() - 0.5) * 0.02; // Add noise

// Generate multiple data streams for different components
const messages = [];

// 1. Top Status Bar Data
messages.push({
    topic: 'update/top_bar',
    payload: {
        scenario_name: sim.scenario,
        sim_time: Math.floor(sim.time_s / 3600).toString().padStart(2, '0') + ':' + 
                 Math.floor((sim.time_s % 3600) / 60).toString().padStart(2, '0') + ':' + 
                 (sim.time_s % 60).toString().padStart(2, '0'),
        realtime_factor: '1.0',
        setpoint_f: sim.setpoint_f.toFixed(1),
        deadband_f: '1.0',
        room_temp_f: sim.room_temp_f.toFixed(1),
        temp_error_f: error_f.toFixed(1),
        lead_unit: 'CRAC-01',
        lag_unit: 'CRAC-02',
        standby_unit: 'CRAC-03'
    }
});

// 2. CRAC Equipment Cards Data
const cracData = [];
Object.keys(sim.crac_states).forEach((id, index) => {
    const crac = sim.crac_states[id];
    const cooling_kw = (crac.output / 100) * [35, 30, 25][index];
    const power_kw = cooling_kw > 0 ? (cooling_kw / 2.8 + 0.5) : 0.5; // COP of 2.8

    cracData.push({
        id: id,
        role: crac.role,
        status: crac.output > 5 ? 'running' : 'off',
        output_pct: Math.round(crac.output),
        supply_temp_f: crac.output > 5 ? (58 + (1 - crac.output/100) * 3).toFixed(1) : '--',
        cooling_kw: cooling_kw.toFixed(1),
        power_kw: power_kw.toFixed(1),
        runtime_h: crac.runtime_h + Math.floor(sim.time_s / 3600),
        start_count: crac.starts,
        alarms: 0
    });
});

messages.push({
    topic: 'update/crac_cards',
    payload: { cracUnits: cracData }
});

// 3. KPI Data
const mae_f = 0.6 + Math.random() * 0.4; // 0.6-1.0¬∞F
const in_band_pct = 95 + Math.random() * 4; // 95-99%
const saturation_pct = pid_output >= 99 ? 5 : 1;
const system_cop = total_cooling > 0 ? (total_cooling * 90) / (lead_output * 0.125 + lag_output * 0.107 + standby_output * 0.089 + 1.5) : 0;

messages.push({
    topic: 'update/kpis',
    payload: {
        kpis: {
            mae_f: mae_f.toFixed(1),
            overshoot_f: Math.max(0, sim.room_temp_f - sim.setpoint_f).toFixed(1),
            in_band_pct: Math.round(in_band_pct),
            saturation_pct: Math.round(saturation_pct),
            stability_index: (0.90 + Math.random() * 0.08).toFixed(2),
            response_time: Math.round(180 + Math.random() * 60),
            runtime_balance: Math.round(45 + Math.random() * 10),
            system_cop: system_cop.toFixed(1),
            cycle_count: Math.round(3 + Math.random() * 3),
            pue: (1.30 + Math.random() * 0.10).toFixed(2),
            utilization_pct: Math.round(65 + Math.random() * 15),
            peak_demand: Math.round(130 + Math.random() * 20)
        },
        lastUpdate: timeString
    }
});

// 4. Alarm Data (occasional alarms for realism)
if (Math.random() < 0.05) { // 5% chance per update
    const alarmTypes = [
        { priority: 'medium', code: 'M-021', asset: 'CRAC-02', description: 'Filter differential high' },
        { priority: 'low', code: 'L-105', asset: 'ROOM-01', description: 'Temperature deviation' },
        { priority: 'high', code: 'H-003', asset: 'CRAC-01', description: 'High discharge pressure' }
    ];

    const alarm = alarmTypes[Math.floor(Math.random() * alarmTypes.length)];
    alarm.timestamp = timestamp;
    alarm.state = 'active';

    sim.alarms.push(alarm);

    // Keep only last 5 alarms
    if (sim.alarms.length > 5) {
        sim.alarms = sim.alarms.slice(-5);
    }
}

// Age alarms
sim.alarms.forEach(alarm => {
    if (alarm.state === 'active' && Math.random() < 0.1) {
        alarm.state = 'acknowledged';
    }
});

const alarmCounts = {
    critical: sim.alarms.filter(a => a.priority === 'critical' && a.state === 'active').length,
    high: sim.alarms.filter(a => a.priority === 'high' && a.state === 'active').length,
    medium: sim.alarms.filter(a => a.priority === 'medium' && a.state === 'active').length,
    low: sim.alarms.filter(a => a.priority === 'low' && a.state === 'active').length
};

messages.push({
    topic: 'update/alarms',
    payload: {
        activeAlarms: sim.alarms,
        alarmCounts: alarmCounts,
        pendingAlarms: Math.floor(Math.random() * 3),
        debounceTime: 30,
        debounceProgress: Math.floor(Math.random() * 100)
    }
});

// 5. Chart Data (for future chart implementation)
messages.push({
    topic: 'update/charts',
    payload: {
        timestamp: sim.time_s,
        room_temp_f: sim.room_temp_f,
        setpoint_f: sim.setpoint_f,
        controller_output: pid_output,
        p_term: p_term,
        i_term: i_term,
        d_term: d_term
    }
});

// Save simulation state
context.simulation = sim;

// Return all messages
return [messages];

// Route data to appropriate dashboard components
const messages = msg.payload;
const outputs = [null, null, null, null, null]; // 5 outputs

messages.forEach(message => {
    switch(message.topic) {
        case 'update/top_bar':
            outputs[0] = message;
            break;
        case 'update/crac_cards':
            outputs[1] = message;
            break;
        case 'update/alarms':
            outputs[2] = message;
            break;
        case 'update/kpis':
            outputs[3] = message;
            break;
        case 'update/charts':
            outputs[4] = message;
            break;
    }
});

return outputs;
